CREATE TABLE makers (id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL);
CREATE TABLE cars (id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, maker_id INTEGER NOT NULL, model VARCHAR(255) NOT NULL, year INTEGER NOT NULL, image_url TEXT, starred BOOLEAN DEFAULT FALSE, FOREIGN KEY (maker_id) REFERENCES makers(id));
UPDATE sqlite_sequence SET seq = (SELECT MAX(id) + 1 FROM makers) WHERE name = 'makers';
